package aula2;

public class variaveis {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int valor; //Criando a variável
		valor = 2023; //Atribuindo valor			
		System.out.println(valor); //Mostrando valor
		
		double troco = 1.55; //Criando e atribuindo valor
		System.out.println(troco);

		String titulo;
		titulo = "Olá, mundo!"; //String entre parenteses
		System.out.println(titulo);
	}

}
